html {
  font-size: 62.5%; }

* {
  box-sizing: border-box; }

body {
  margin: 0;
  padding: 0;
  height: 100vh;
  background: #0e3434;
  color: seashell;
  font-size: 16px;
  display: grid;
  grid-template-rows: 100px 1fr;
  font-family: "Montserrat", sans-serif; }

main {
  width: 100%;
  margin: 0 auto;
  height: 100%;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr; }

section {
  padding: 20px; }
  section .section-title {
    font-style: italic;
    margin: 0;
    margin-bottom: 15px;
    font-size: 18px;
    letter-spacing: 0;
    border-bottom: 1px solid seashell; }

@media (min-width: 1150px) {
  main {
    grid-template-columns: 300px 1fr 1fr 300px; }
  section {
    padding: 20px; }
    section .section-title {
      font-size: 22px;
      letter-spacing: 3px; } }

.menu {
  grid-column: 3 / 5; }

.controls {
  margin-bottom: 10px;
  padding: 10px 0;
  border: 1px solid seashell;
  border-radius: 15px;
  text-align: center; }
  .controls h6 {
    margin: 0;
    margin-bottom: 10px;
    font-size: 18px; }
  .controls__button {
    height: 35px;
    width: 35px;
    margin: 0 10px;
    border: 1px solid seashell;
    border-radius: 10px;
    background: none;
    outline: none;
    font: inherit;
    color: seashell;
    font-weight: bold; }
    .controls__button--add {
      background: cadetblue; }
    .controls__button--remove {
      background: tomato; }
    .controls__button--add:hover, .controls__button--remove:hover {
      cursor: pointer;
      background-color: #8fbbbd; }
    .controls__button--remove:hover {
      background-color: #fa2600; }

@media (min-width: 700px) {
  .menu {
    grid-column: 4 / 5; } }

@media (min-width: 1150px) {
  .controls {
    padding: 10px 20px;
    margin-bottom: 20px; }
    .controls__button {
      height: 45px;
      width: 45px; } }

.stats {
  grid-column: 1 / 3; }

.stats__container {
  background-color: #2f5a5a;
  border-radius: 15px;
  width: 80%;
  padding: 10px; }

.stats-item {
  padding: 10px 0;
  font-size: 16px; }

.stats-summary {
  margin-top: 8px;
  border-top: 1px dashed seashell;
  font-weight: bold; }

@media (min-width: 1150px) {
  .stats__container {
    padding: 20px;
    margin-bottom: 20px; }
  .stats-item {
    padding: 10px 0;
    font-size: 22px; }
  .stats-summary {
    margin-top: 20px; } }

@media (min-width: 700px) {
  .stats {
    grid-column: 1 / 2; } }

.aquarium {
  display: flex;
  align-items: center;
  justify-content: center;
  grid-column: 1 / -1;
  grid-row: 1; }
  .aquarium__bowl {
    height: 300px;
    width: 300px;
    border-radius: 50%;
    background: cyan;
    position: relative;
    overflow: hidden;
    z-index: -1; }
  .aquarium__bowl:after, .aquarium__bowl:before {
    position: absolute;
    content: '';
    background: #0e3434;
    height: 40px;
    width: 100%;
    left: 0;
    bottom: -20px;
    z-index: 10; }
  .aquarium__bowl:before {
    left: 0;
    bottom: calc(100% - 40px); }

@media (min-width: 700px) {
  .aquarium {
    grid-column: 2 / 4; } }

@media (min-width: 1150px) {
  .aquarium__bowl {
    height: 500px;
    width: 500px; } }

header {
  padding: 10px 20px; }
  header h1 {
    text-transform: uppercase;
    text-align: center;
    letter-spacing: 6px;
    margin: 0;
    font-size: 30px; }

@media (min-width: 1150px) {
  header h1 {
    font-size: 70px; } }

.fish {
  height: 35px;
  width: 35px;
  position: absolute;
  animation: floatingY alternate 2s infinite ease-in-out;
  background-position: center;
  background-size: cover; }
  .fish:nth-child(4n + 1) {
    animation-duration: calc(1.2s * 1.75); }
  .fish:nth-child(4n + 2) {
    animation-duration: calc(1.2s * 2.75); }
  .fish:nth-child(4n + 3) {
